import React, { useState } from "react";
import "./EditUserProfile.css";

interface EditUserProfileProps {
  name?: string;
  username: string;
  bio?: string;
  location?: string;
  interests?: string[];
  classes?: string[];
  onSave: (updated: {
    name?: string;
    username: string;
    bio?: string;
    location?: string;
    interests?: string[];
    classes?: string[];
  }) => void;
}

const EditUserProfile: React.FC<EditUserProfileProps> = ({
  name = "",
  username,
  bio = "",
  location = "",
  interests = [],
  classes = [],
  onSave,
}) => {
  // Local state for form inputs
  const [formName, setFormName] = useState(name);
  const [formUsername, setFormUsername] = useState(username);
  const [formBio, setFormBio] = useState(bio);
  const [formLocation, setFormLocation] = useState(location);
  const [formInterests, setFormInterests] = useState(interests);
  const [formClasses, setFormClasses] = useState(classes);

  // Input handlers for interests and classes (comma separated)
  const handleInterestsChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormInterests(
      e.target.value
        .split(",")
        .map((i) => i.trim())
        .filter((i) => i.length > 0)
    );
  };

  const handleClassesChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormClasses(
      e.target.value
        .split(",")
        .map((c) => c.trim())
        .filter((c) => c.length > 0)
    );
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave({
      name: formName,
      username: formUsername,
      bio: formBio,
      location: formLocation,
      interests: formInterests,
      classes: formClasses,
    });
  };

  return (
    <form className="edit-profile-container" onSubmit={handleSubmit}>
      <h2>Edit Profile</h2>

      <label htmlFor="name">Name</label>
      <input
        id="name"
        type="text"
        value={formName}
        onChange={(e) => setFormName(e.target.value)}
        placeholder="Enter your full name"
      />

      <label htmlFor="username">Username</label>
      <input
        id="username"
        type="text"
        value={formUsername}
        onChange={(e) => setFormUsername(e.target.value)}
        placeholder="Enter your username"
        required
      />

      <label htmlFor="location">Location</label>
      <input
        id="location"
        type="text"
        value={formLocation}
        onChange={(e) => setFormLocation(e.target.value)}
        placeholder="Enter your location"
      />

      <label htmlFor="bio">Bio</label>
      <textarea
        id="bio"
        value={formBio}
        onChange={(e) => setFormBio(e.target.value)}
        placeholder="Write something about yourself"
      />

      <label htmlFor="interests">Interests (comma separated)</label>
      <input
        id="interests"
        type="text"
        value={formInterests.join(", ")}
        onChange={handleInterestsChange}
        placeholder="e.g. Hiking, Music, Startups"
      />

      <label htmlFor="classes">Classes (comma separated)</label>
      <input
        id="classes"
        type="text"
        value={formClasses.join(", ")}
        onChange={handleClassesChange}
        placeholder="e.g. CS1010, MA1521"
      />

      <button type="submit" className="save-btn">
        Save Changes
      </button>
    </form>
  );
};

export default EditUserProfile;
