import React from "react";
import "./UserProfile.css";

interface UserProfileProps {
  name: string;
  username: string;
  bio?: string;
  avatarUrl: string; // avatar image URL or data URI
  location?: string; // dormitory/residence
  interests?: string[];
  classes?: string[];
}

const UserProfile: React.FC<UserProfileProps> = ({
  name,
  username,
  bio,
  avatarUrl,
  location,
  interests = [],
  classes = [],
}) => {
  return (
    <div className="user-profile-container">
      <aside className="sidebar">
        <img src={avatarUrl} alt={`${name} avatar`} className="avatar" />
        <h1 className="name">{name}</h1>
        <h2 className="username">@{username}</h2>
        {location && <p className="location">üè† {location}</p>}

        {interests.length > 0 && (
          <section className="interests-section">
            <h3>Interests</h3>
            <ul className="interests-list">
              {interests.map((interest, idx) => (
                <li key={idx} className="interest-item">
                  #{interest}
                </li>
              ))}
            </ul>
          </section>
        )}

        {classes.length > 0 && (
          <section className="classes-section">
            <h3>Classes</h3>
            <ul className="classes-list">
              {classes.map((cls, idx) => (
                <li key={idx} className="class-item">
                  {cls}
                </li>
              ))}
            </ul>
          </section>
        )}
      </aside>

      <main className="main-content">
        <section className="bio-section">
          <h3>About Me</h3>
          <p>{bio || "This user hasn't added a bio yet."}</p>
        </section>

        {/* Placeholder for other extensions like messaging, events, memories, etc. */}
      </main>
    </div>
  );
};

export default UserProfile;
