import React from "react";

// type ChatListItemProps = {
//     name: string;
//     lastMessage: string;
// };

// function ChatList({ name, lastMessage }: ChatListItemProps) {
//     return ;
// }

// export default ChatList;

interface Friend {
  id: string;
  name: string;
  lastMessage: string;
}

export const mockFriends = [
  { id: "1", name: "Alice", lastMessage: "See you soon!" },
  { id: "2", name: "Bob", lastMessage: "Thanks!" },
  { id: "3", name: "Charlie", lastMessage: "Okay cool." },
];

interface ChatListProps {
  friends: Friend[];
  onSelect: (id: string) => void;
}

function ChatList({ friends, onSelect }: ChatListProps): React.ReactElement {
  return (
    <div style={{ overflowY: "auto", flexGrow: 1 }}>
      {friends.map((friend) => (
        <div
          key={friend.id}
          onClick={() => onSelect(friend.id)}
          style={{
            padding: "10px",
            cursor: "pointer",
            borderBottom: "1px solid #333",
            color: "white",
          }}
        >
          <div style={{ fontWeight: "bold" }}>{friend.name}</div>
          <div style={{ fontSize: "0.875rem", color: "#ccc" }}>{friend.lastMessage}</div>
        </div>
      ))}
    </div>
  );
}
export default ChatList;
