import React, { useState } from "react";
import SearchBar from "../shared/SearchBar";
import ChatList from "../chat/ChatList";

const mockFriends = [
  { id: "1", name: "Alice", lastMessage: "See you soon!" },
  { id: "2", name: "Bob", lastMessage: "Thanks!" },
  { id: "3", name: "Charlie", lastMessage: "Okay cool." },
];

function Sidebar(): JSX.Element {
  const [search, setSearch] = useState("");
  const [selectedId, setSelectedId] = useState<string | null>(null);

  const filteredFriends = mockFriends.filter((f) =>
    f.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div
      style={{
        width: "300px",
        height: "100vh",
        backgroundColor: "#111",
        display: "flex",
        flexDirection: "column",
        borderRight: "1px solid #333",
      }}
    >
      <SearchBar value={search} onChange={setSearch} />
      <ChatList friends={filteredFriends} onSelect={setSelectedId} />
    </div>
  );
}

export default Sidebar;
