import React from 'react';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from "../../App";
import { Message } from '../../types';


function MessageInput() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState("");
  const [currentUserId, setCurrentUserId] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setCurrentUserId(user.id);
      }
    };
    fetchUser();
  }, []);

  useEffect(() => {
    const fetchMessages = async () => {
      const { data, error } = await supabase
        .from("messages")
        .select("content, created_at, username, avatar_url")
        .order("created_at", { ascending: true });
      if (!error && data) {
        setMessages(data as Message[]);
      } 
    };

    fetchMessages();
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const { data: { user } } = await supabase.auth.getUser();
    if (!user) {
      alert("Please log in.");
      setTimeout(() => {
        navigate("/login"); 
      }, 2000);
      return;
    }

  const userMsg: Message = {
    id: `${Date.now()}`, 
    user_id: user.id,   
    content: input.trim(),
    username: user.user_metadata?.username || "anonymous",
    avatar_url: user.user_metadata?.avatar_url || "",
    created_at: new Date().toISOString()
  };

    setMessages((prev: Message[]) => [...prev, userMsg]);

   const { error } = await supabase.from("messages").insert({
    user_id: user.id,
    content: userMsg.content,
    username: userMsg.username,
    avatar_url: userMsg.avatar_url
  });

    if (error) console.error("Something went wrong!!!", error);

    setTimeout(() => {
      const botMsg: Message = {
        id: messages.length + 2,
        sender: "other user",
        content: `not implemented yet:`,
    };

    setMessages((prev: Message[]) => [...prev, botMsg]);
    }, 500);

    setInput("");
  };


  function isPinMessage(id: number) {
    return id === 1;
  }
  return (
    <div style={{ maxWidth: "800px", margin: "auto", padding: "20px" }}>
        <h2>Chat Messages</h2>
        <div
            style={{
                border: "1px solid #ccc",
                padding: "10px",
                height: "500px",
                overflowY: "auto",
                marginBottom: "10px",
            }}
        >
        {messages.map((msg) => (
            <div
                key={msg.id}
                style={{
                    display: "flex",
                    position: isPinMessage(msg.id) ? "sticky" : "relative",
                    // justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
                    backgroundColor: msg.user_id === currentUserId ? "#abcdef" : "#f8d7da",
                    maxWidth: "60%",
                    width: "fit-content", 
                    padding: "10px",
                    margin: "5px 0",
                    wordBreak: "break-word",
                    marginLeft: msg.user_id === currentUserId ? "auto" : "0", // push user messages to right
                    marginRight: msg.user_id !== currentUserId ? "auto" : "0", 
                    color: "black",
                }}
            >
            {msg.content}
            </div>
        ))}
        </div>
        <div style={{display: "flex"}}><input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            style={{ width: "80%", marginRight: "10px" }}/>
            <button onClick={sendMessage} style={{width: "20%"}}>Send</button>
        </div>
    </div>
  );
}

export default MessageInput;
